    public GridDistanceProcessing (byte[] m, int h, int l) {
        map = m;
        length = h;
        width = l;
        lengthModifier = mapLength / length;
        widthModifier = mapWidth / width;
    }

    public int currentIndex (Pose2d pose) {
        double OriginalX = pose.getX();
        double OriginalY = pose.getY();
        int NewX = (int) (OriginalX / lengthModifier);
        int NewY = (int) (OriginalY / widthModifier);
        return NewX + (NewY * length);
    }

